import React from 'react';
import Node from './Node';
import './WorkflowCanvas.css';

const WorkflowCanvas = ({ workflow, onAddNode, onDeleteNode, onEditNode }) => {
  const renderNode = (nodeId, level = 0) => {
    const node = workflow.nodes[nodeId];
    if (!node) return null;

    return (
      <div key={nodeId} className="node-level" style={{ marginLeft: `${level * 200}px` }}>
        <Node
          node={node}
          onAddNode={onAddNode}
          onDeleteNode={onDeleteNode}
          onEditNode={onEditNode}
        />

        {/* Render children */}
        {node.type === 'branch' ? (
          <div className="branch-children">
            {Object.entries(node.branches || {}).map(([branchName, childId]) => (
              <div key={branchName} className="branch">
                <div className="branch-label">{branchName}</div>
                {childId && renderNode(childId, level + 1)}
                {!childId && (
                  <div className="empty-branch">
                    <button 
                      className="add-to-branch-btn"
                      onClick={() => onAddNode({ parentId: node.id, branchName, position: 0 })}
                    >
                      + Add to {branchName} branch
                    </button>
                  </div>
                )}
              </div>
            ))}
          </div>
        ) : (
          <div className="children">
            {node.children.map((childId, index) => renderNode(childId, level + 1))}
          </div>
        )}

        {/* Add node button for non-end nodes */}
        {node.type !== 'end' && node.type !== 'branch' && (
          <div className="add-node-container">
            <button 
              className="add-node-btn"
              onClick={() => onAddNode({ parentId: node.id, position: node.children.length })}
            >
              + Add Node
            </button>
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="workflow-canvas">
      {renderNode(workflow.rootId)}
    </div>
  );
};

export default WorkflowCanvas;
