import React, { useState } from 'react';
import './AddNodeMenu.css';

const AddNodeMenu = ({ parentId, branchName, position, onAddNode, onClose }) => {
  const [selectedType, setSelectedType] = useState('');

  const handleAdd = () => {
    if (selectedType) {
      onAddNode(parentId, selectedType, branchName, position);
    }
  };

  return (
    <div className="modal-overlay">
      <div className="add-node-menu">
        <h3>Add New Node</h3>

        <div className="node-types">
          <label className="node-type-option">
            <input
              type="radio"
              name="nodeType"
              value="action"
              checked={selectedType === 'action'}
              onChange={(e) => setSelectedType(e.target.value)}
            />
            <div className="type-card">
              <h4>Action Node</h4>
              <p>A single step/task with one outgoing connection</p>
            </div>
          </label>

          <label className="node-type-option">
            <input
              type="radio"
              name="nodeType"
              value="branch"
              checked={selectedType === 'branch'}
              onChange={(e) => setSelectedType(e.target.value)}
            />
            <div className="type-card">
              <h4>Branch Node</h4>
              <p>A decision point with multiple outgoing connections</p>
            </div>
          </label>

          <label className="node-type-option">
            <input
              type="radio"
              name="nodeType"
              value="end"
              checked={selectedType === 'end'}
              onChange={(e) => setSelectedType(e.target.value)}
            />
            <div className="type-card">
              <h4>End Node</h4>
              <p>The final step with no outgoing connections</p>
            </div>
          </label>
        </div>

        <div className="menu-buttons">
          <button 
            className="add-btn"
            onClick={handleAdd}
            disabled={!selectedType}
          >
            Add Node
          </button>
          <button className="cancel-btn" onClick={onClose}>
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default AddNodeMenu;
